import { useEffect, useMemo, useState } from 'react'
import { registrationsList, bookingsList, toolsList, feedbackList, usersList } from '../../services/api'
import { Link } from 'react-router-dom'
import { IconDashboard, IconList, IconTools, IconFeedback, IconUsers, IconSearch, IconRefresh } from '../../components/Icons'
import Breadcrumbs from '../../components/Breadcrumbs'

function TabBar({ tab, setTab }) {
  const tabs = [
    { key: 'Dashboard', icon: <IconDashboard />, label: 'Dashboard' },
    { key: 'Registrations', icon: <IconList />, label: 'Registrations' },
    { key: 'Bookings', icon: <IconList />, label: 'Bookings' },
    { key: 'Tools', icon: <IconTools />, label: 'Tools' },
    { key: 'Feedback', icon: <IconFeedback />, label: 'Feedback' },
    { key: 'Users', icon: <IconUsers />, label: 'Users' },
  ]
  return (
    <div className="tabs">
      {tabs.map(t => (
        <button key={t.key} className={`tab ${tab===t.key?'active':''}`} onClick={()=>setTab(t.key)}>
          <span style={{ display:'inline-flex', alignItems:'center', gap:6 }}>
            {t.icon}<span>{t.label}</span>
          </span>
        </button>
      ))}
    </div>
  )
}

export default function AdminDashboard() {
  const [tab,setTab] = useState('Dashboard')
  const [data,setData] = useState([])
  const [err,setErr] = useState('')
  const [search,setSearch] = useState('')
  const [page,setPage] = useState(1)
  const pageSize = 10
  const [metrics,setMetrics] = useState({ registrations:0, bookings:0, tools:0, feedback:0, users:0 })

  useEffect(() => {
    (async () => {
      try {
        setErr('')
        if (tab==='Dashboard') {
          // Load counts for cards
          const [r,b,t,f,u] = await Promise.all([
            registrationsList().catch(()=>({ registrations:[] })),
            bookingsList().catch(()=>({ bookings:[] })),
            toolsList().catch(()=>({ tools:[] })),
            feedbackList().catch(()=>({ feedback:[] })),
            usersList().catch(()=>({ users:[] })),
          ])
          setMetrics({
            registrations: (r.registrations||[]).length,
            bookings: (b.bookings||[]).length,
            tools: (t.tools||[]).length,
            feedback: (f.feedback||[]).length,
            users: (u.users||[]).length,
          })
          setData([])
        } else if (tab==='Registrations') {
          const { registrations } = await registrationsList(); setData(registrations)
        } else if (tab==='Bookings') {
          const { bookings } = await bookingsList(); setData(bookings)
        } else if (tab==='Tools') {
          const { tools } = await toolsList(); setData(tools)
        } else if (tab==='Feedback') {
          const { feedback } = await feedbackList(); setData(feedback)
        } else if (tab==='Users') {
          try {
            const { users } = await usersList();
            // Normalize role capitalization for display
            setData(users.map(u => ({ ...u, role: (u.role||'').charAt(0).toUpperCase() + (u.role||'').slice(1) })))
          } catch (e) {
            setErr(e.message)
            setData([])
          }
        }
        setPage(1)
        setSearch('')
      } catch (e) { setErr(e.message); setData([]) }
    })()
  }, [tab])

  const filtered = useMemo(() => {
    if (!search) return data
    const q = search.toLowerCase()
    return data.filter(row => Object.values(row).some(v => String(v).toLowerCase().includes(q)))
  }, [data, search])

  const paged = useMemo(() => {
    const start = (page-1)*pageSize
    return filtered.slice(start, start+pageSize)
  }, [filtered, page])

  const totalPages = Math.max(1, Math.ceil(filtered.length / pageSize))

  return (
    <div>
      <Breadcrumbs items={[{label:'Admin Panel', to:'/admin'}, {label: tab}]} />
      <div className="page-header">
        <h2>Admin Panel</h2>
        <div className="actions">
          {tab==='Users' && <Link className="btn" to="/users">Open User Management</Link>}
        </div>
      </div>
      <TabBar tab={tab} setTab={setTab} />

      {err && <p className="error">{err}</p>}

      {/* Dashboard cards */}
      {tab==='Dashboard' && (
        <div className="grid-3" style={{ marginTop:12 }}>
          <div className="metric-card"><div className="metric-label">Registrations</div><div className="metric-value">{metrics.registrations}</div></div>
          <div className="metric-card"><div className="metric-label">Bookings</div><div className="metric-value">{metrics.bookings}</div></div>
          <div className="metric-card"><div className="metric-label">Tools</div><div className="metric-value">{metrics.tools}</div></div>
          <div className="metric-card"><div className="metric-label">Feedback</div><div className="metric-value">{metrics.feedback}</div></div>
          <div className="metric-card"><div className="metric-label">Users</div><div className="metric-value">{metrics.users}</div></div>
        </div>
      )}

      {tab!=='Dashboard' && (
        <>
          <div className="card" style={{ marginTop: 12 }}>
            <div className="row" style={{ display:'flex', gap:8 }}>
              <div style={{ flex:1, display:'flex', alignItems:'center', gap:8 }}>
                <IconSearch />
                <input placeholder={`Search ${tab}...`} value={search} onChange={e=>{setSearch(e.target.value); setPage(1)}} />
              </div>
              <button className="btn ghost" onClick={()=>{setSearch(''); setPage(1)}}><IconRefresh /> Clear</button>
            </div>
            {filtered.length === 0 ? (
              <p className="hint">No data</p>
            ) : (
              <div className="table-wrap">
                <table className="table">
                  <thead>
                    <tr>
                      {tab==='Users' ? (
                        <>
                          <th>Name</th>
                          <th>Email</th>
                          <th>Role</th>
                          <th>Joined</th>
                        </>
                      ) : (
                        Object.keys(filtered[0]).filter(k=>k!=='password' && k!=='__v').slice(0,6).map(k=> <th key={k}>{k}</th>)
                      )}
                    </tr>
                  </thead>
                  <tbody>
                    {tab==='Users' ? (
                      paged.map(u => (
                        <tr key={u._id || u.id}>
                          <td>{u.name}</td>
                          <td>{u.email}</td>
                          <td><span className={`badge ${String(u.role).toLowerCase()}`}>{u.role}</span></td>
                          <td>{u.createdAt ? new Date(u.createdAt).toLocaleString() : '-'}</td>
                        </tr>
                      ))
                    ) : (
                      paged.map((row,i)=> (
                        <tr key={row._id || i}>
                          {Object.keys(row).filter(k=>k!=='password' && k!=='__v').slice(0,6).map(k=> <td key={k}>{String(row[k])}</td>)}
                        </tr>
                      ))
                    )}
                  </tbody>
                </table>
              </div>
            )}
            <div className="pagination">
              <button className="btn ghost" onClick={()=>setPage(p=>Math.max(1,p-1))}>Prev</button>
              <span className="hint">Page {page} of {totalPages}</span>
              <button className="btn ghost" onClick={()=>setPage(p=>Math.min(totalPages,p+1))}>Next</button>
            </div>
          </div>
        </>
      )}
    </div>
  )
}
