import { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { useAuth } from '../../context/AuthContext'

export default function Login() {
  const [email,setEmail] = useState('member@garden.local')
  const [password,setPassword] = useState('Member@123')
  const [err,setErr] = useState('')
  const nav = useNavigate()
  const { login } = useAuth()

  const submit = (e)=> {
    e.preventDefault()
    try { login({email,password}); nav('/dashboard') }
    catch (e) { setErr(e.message) }
  }

  return (
    <div className="card">
      <h2>Login</h2>
      {err && <p className="error">{err}</p>}
      <form onSubmit={submit}>
        <label>Email</label>
        <input value={email} onChange={e=>setEmail(e.target.value)} />
        <label>Password</label>
        <input type="password" value={password} onChange={e=>setPassword(e.target.value)} />
        <button className="btn">Login</button>
      </form>
      <p>Seeded users: admin@garden.local / Admin@123, manager@garden.local / Manager@123, member@garden.local / Member@123</p>
      <div className="auth-footer">
        <Link to="/home" className="btn ghost">Back to Home</Link>
      </div>
    </div>
  )
}
