import React from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import RoleBadge from './RoleBadge';

export default function NavBar() {
  const { user, logout } = useAuth()
  return (
    <nav className="nav">
      <Link className="brand" to="/home">Garden Scheduler</Link>
      {user ? (
        <>
          <div className="nav-links">
            {(user.role==='Member' || user.role==='Manager') && (
              <>
                <NavLink to="/form/register">Register</NavLink>
                <NavLink to="/form/booking">Book Plot</NavLink>
                <NavLink to="/form/confirm-tools">Tools</NavLink>
                <NavLink to="/form/feedback">Feedback</NavLink>
                <NavLink to="/my-bookings">My Bookings</NavLink>
                <NavLink to="/reports/occupancy">Reports</NavLink>
              </>
            )}
            {user.role==='Manager' && <NavLink to="/manager">Manager Dashboard</NavLink>}
            {/* Admin navigation */}
            {user.role === 'Admin' && (
              <div className="admin-nav">
                <NavLink to="/admin/sessions">Sessions</NavLink>
                <NavLink to="/users">Users</NavLink>
              </div>
            )}
          </div>
          <div className="nav-right">
            <RoleBadge role={user.role} />
            <span className="user">{user.name}</span>
            <button
              className="btn"
              onClick={() => {
                logout()
                navigate('/home')
              }}
            >
              Logout
            </button>
          </div>
        </>
      ) : (
        <div className="nav-links">
          <NavLink to="/login">Login</NavLink>
          <NavLink to="/signup">Signup</NavLink>
        </div>
      )}
    </nav>
  )
}
