import React, { useState, useEffect } from 'react';
import { bookingsList, usersList, plotsList } from '../../services/api';

export default function ManagerReports() {
  const [reports, setReports] = useState({
    totalUsers: 0,
    activeBookings: 0,
    availablePlots: 0,
    occupiedPlots: 0,
    recentBookings: [],
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState('overview');

  useEffect(() => {
    const fetchReportData = async () => {
      try {
        setLoading(true);

        // Fetch all necessary data in parallel
        const [bookingsRes, usersRes, plotsRes] = await Promise.all([
          bookingsList(),
          usersList(),
          plotsList()
        ]);

        const bookings = bookingsRes.bookings || [];
        const users = usersRes.users || [];
        const plots = plotsRes.plots || [];

        // Calculate report data
        const now = new Date();
        const recentBookings = bookings
          .filter(booking => new Date(booking.endDate) >= now)
          .sort((a, b) => new Date(a.startDate) - new Date(b.startDate))
          .slice(0, 5);

        setReports({
          totalUsers: users.length,
          activeBookings: bookings.filter(b => new Date(b.endDate) >= now).length,
          availablePlots: plots.filter(p => p.status === 'available').length,
          occupiedPlots: plots.filter(p => p.status === 'occupied').length,
          recentBookings,
          totalPlots: plots.length,
        });
      } catch (err) {
        setError('Failed to load reports. Please try again.');
        console.error('Error loading reports:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchReportData();
  }, []);

  const renderOverview = () => (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
      <StatCard 
        title="Total Users" 
        value={reports.totalUsers} 
        icon="👥"
        color="blue"
      />
      <StatCard 
        title="Active Bookings" 
        value={reports.activeBookings} 
        icon="📅"
        color="green"
      />
      <StatCard 
        title="Available Plots" 
        value={reports.availablePlots} 
        subValue={`of ${reports.totalPlots}`}
        icon="🏡"
        color="indigo"
      />
      <StatCard 
        title="Occupied Plots" 
        value={reports.occupiedPlots} 
        subValue={`of ${reports.totalPlots}`}
        icon="🔒"
        color="yellow"
      />
    </div>
  );

  const renderRecentBookings = () => (
    <div className="bg-white rounded-lg shadow overflow-hidden">
      <div className="px-6 py-4 border-b border-gray-200">
        <h3 className="text-lg font-medium text-gray-900">Upcoming Bookings</h3>
      </div>
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Plot</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">User</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Start Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">End Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {reports.recentBookings.length > 0 ? (
              reports.recentBookings.map((booking) => (
                <tr key={booking._id}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {booking.plot?.plotNumber || 'N/A'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {booking.user?.name || 'Unknown User'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(booking.startDate).toLocaleDateString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {new Date(booking.endDate).toLocaleDateString()}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                      ${
                        booking.status === 'confirmed' 
                          ? 'bg-green-100 text-green-800' 
                          : booking.status === 'pending'
                          ? 'bg-yellow-100 text-yellow-800'
                          : 'bg-red-100 text-red-800'
                      }`}>
                      {booking.status}
                    </span>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="px-6 py-4 text-center text-sm text-gray-500">
                  No upcoming bookings found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <div style={{ padding: '2rem', maxWidth: '1400px', margin: '0 auto' }}>
      <div style={{ marginBottom: '2rem' }}>
        <h1 style={{ fontSize: '1.875rem', fontWeight: '700', color: '#111827', marginBottom: '0.5rem' }}>
          Reports & Analytics
        </h1>
        <p style={{ color: '#6b7280', marginBottom: '1.5rem' }}>
          View system statistics and generate reports
        </p>
      </div>

      {error && (
        <div style={{
          background: '#fee2e2',
          color: '#b91c1c',
          padding: '1rem',
          borderRadius: '0.5rem',
          marginBottom: '1.5rem',
        }}>
          {error}
        </div>
      )}

      <div className="mb-6">
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            <button
              onClick={() => setActiveTab('overview')}
              className={`${
                activeTab === 'overview'
                  ? 'border-indigo-500 text-indigo-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
            >
              Overview
            </button>
            <button
              onClick={() => setActiveTab('bookings')}
              className={`${
                activeTab === 'bookings'
                  ? 'border-indigo-500 text-indigo-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
            >
              Bookings
            </button>
            <button
              onClick={() => setActiveTab('users')}
              className={`${
                activeTab === 'users'
                  ? 'border-indigo-500 text-indigo-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
            >
              Users
            </button>
            <button
              onClick={() => setActiveTab('plots')}
              className={`${
                activeTab === 'plots'
                  ? 'border-indigo-500 text-indigo-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm`}
            >
              Plots
            </button>
          </nav>
        </div>
      </div>

      {loading ? (
        <div className="flex justify-center items-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-indigo-500"></div>
        </div>
      ) : (
        <div>
          {activeTab === 'overview' && (
            <>
              {renderOverview()}
              {renderRecentBookings()}
            </>
          )}
          {activeTab === 'bookings' && (
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Bookings Report</h3>
              <p className="text-gray-600">Detailed booking reports coming soon.</p>
            </div>
          )}
          {activeTab === 'users' && (
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Users Report</h3>
              <p className="text-gray-600">Detailed user reports coming soon.</p>
            </div>
          )}
          {activeTab === 'plots' && (
            <div className="bg-white rounded-lg shadow p-6">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Plots Report</h3>
              <p className="text-gray-600">Detailed plot reports coming soon.</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

