import { useState, useEffect } from 'react';
import FormRow from '../../components/FormRow';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { registrationsCreate } from '../../services/api';

export default function MemberRegistration() {
  const { user } = useAuth();
  const nav = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [values, setValues] = useState({
    memberName: user?.name || '', 
    email: user?.email || '', 
    phone: '',
    address: '', 
    city: '', 
    state: '', 
    pincode: '',
    age: '', 
    gender: 'Prefer not to say', 
    experience: 'Beginner',
    preferredTime: 'Morning', 
    emergencyName: '', 
    emergencyPhone: '',
    consent: false, 
    newsletter: true,
    idProofUrl: '', 
    gardenRulesAccepted: false,
    toolsTraining: 'No', 
    disabilitySupport: 'No', 
    notes: ''
  });

  useEffect(() => {
    // Load saved draft from localStorage if exists
    const savedDraft = localStorage.getItem('draft_register');
    if (savedDraft) {
      try {
        const parsedDraft = JSON.parse(savedDraft);
        setValues(prev => ({
          ...prev,
          ...parsedDraft,
          // Ensure required fields are not overridden by empty saved values
          memberName: parsedDraft.memberName || user?.name || '',
          email: parsedDraft.email || user?.email || ''
        }));
      } catch (e) {
        console.error('Failed to parse saved draft:', e);
      }
    }
    setIsLoading(false);
  }, [user]);

  const setField = (field, value) => {
    const newValues = { ...values, [field]: value };
    setValues(newValues);
    // Save to localStorage
    localStorage.setItem('draft_register', JSON.stringify(newValues));
  };

  const reset = () => {
    const defaultValues = {
      memberName: user?.name || '', 
      email: user?.email || '', 
      phone: '',
      address: '', 
      city: '', 
      state: '', 
      pincode: '',
      age: '', 
      gender: 'Prefer not to say', 
      experience: 'Beginner',
      preferredTime: 'Morning', 
      emergencyName: '', 
      emergencyPhone: '',
      consent: false, 
      newsletter: true,
      idProofUrl: '', 
      gardenRulesAccepted: false,
      toolsTraining: 'No', 
      disabilitySupport: 'No',
      notes: ''
    };
    setValues(defaultValues);
    localStorage.setItem('draft_register', JSON.stringify(defaultValues));
  };

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState(null);

  const next = async (e) => {
    e.preventDefault();
    setError(null);

    // Basic validation
    if (!values.memberName || !values.email) {
      setError('Name and Email are required fields');
      return;
    }

    if (!values.consent || !values.gardenRulesAccepted) {
      setError('Please accept the garden rules and data usage consent');
      return;
    }

    try {
      setIsSubmitting(true);
      const response = await registrationsCreate(values);

      // Clear the draft on successful submission
      localStorage.removeItem('draft_register');

      // Navigate to booking page
      nav('/book-plot');
    } catch (err) {
      console.error('Registration error:', err);
      setError(err.message || 'Failed to submit registration. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isLoading) {
    return <div className="card">Loading...</div>;
  }

  return (
    <div className="card">
      <h2>Member Registration</h2>
      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}
      <form onSubmit={next}>
        <FormRow label="Full Name" required>
          <input 
            value={values.memberName} 
            onChange={e => setField('memberName', e.target.value)}
            required
          />
        </FormRow>
        <FormRow label="Email" required>
          <input 
            type="email" 
            value={values.email} 
            onChange={e => setField('email', e.target.value)} 
            required 
          />
        </FormRow>
        <FormRow label="Phone">
          <input 
            type="tel" 
            value={values.phone} 
            onChange={e => setField('phone', e.target.value)} 
          />
        </FormRow>
        <FormRow label="Address">
          <input 
            value={values.address} 
            onChange={e => setField('address', e.target.value)} 
          />
        </FormRow>
        <div className="grid-3">
          <FormRow label="City">
            <input value={values.city} onChange={e => setField('city', e.target.value)} />
          </FormRow>
          <FormRow label="State">
            <input value={values.state} onChange={e => setField('state', e.target.value)} />
          </FormRow>
          <FormRow label="PIN Code">
            <input value={values.pincode} onChange={e => setField('pincode', e.target.value)} />
          </FormRow>
        </div>
        <div className="grid-3">
          <FormRow label="Age">
            <input 
              type="number" 
              min="1" 
              value={values.age} 
              onChange={e => setField('age', e.target.value)} 
            />
          </FormRow>
          <FormRow label="Gender">
            <select 
              value={values.gender} 
              onChange={e => setField('gender', e.target.value)}
              className="form-select"
            >
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Non-binary">Non-binary</option>
              <option value="Prefer not to say">Prefer not to say</option>
            </select>
          </FormRow>
          <FormRow label="Experience">
            <select 
              value={values.experience} 
              onChange={e => setField('experience', e.target.value)}
              className="form-select"
            >
              <option value="Beginner">Beginner</option>
              <option value="Intermediate">Intermediate</option>
              <option value="Advanced">Advanced</option>
            </select>
          </FormRow>
        </div>
        <div className="grid-3">
          <FormRow label="Preferred Time">
            <select 
              value={values.preferredTime} 
              onChange={e => setField('preferredTime', e.target.value)}
              className="form-select"
            >
              <option value="Morning">Morning</option>
              <option value="Afternoon">Afternoon</option>
              <option value="Evening">Evening</option>
            </select>
          </FormRow>
          <FormRow label="Emergency Contact Name">
            <input 
              value={values.emergencyName} 
              onChange={e => setField('emergencyName', e.target.value)} 
            />
          </FormRow>
          <FormRow label="Emergency Contact Phone">
            <input 
              type="tel" 
              value={values.emergencyPhone} 
              onChange={e => setField('emergencyPhone', e.target.value)} 
            />
          </FormRow>
        </div>
        <div className="grid-3">
          <FormRow label="ID Proof URL (optional)">
            <input 
              type="url" 
              value={values.idProofUrl} 
              onChange={e => setField('idProofUrl', e.target.value)} 
              placeholder="https://example.com/id-proof.jpg"
            />
          </FormRow>
          <FormRow label="Tools Training">
            <select 
              value={values.toolsTraining} 
              onChange={e => setField('toolsTraining', e.target.value)}
              className="form-select"
            >
              <option value="No">No</option>
              <option value="Yes">Yes</option>
            </select>
          </FormRow>
        </div>

        <div className="form-group">
          <label className="check">
            <input 
              type="checkbox" 
              checked={values.newsletter} 
              onChange={e => setField('newsletter', e.target.checked)} 
            /> 
            Subscribe to newsletter
          </label>
          <label className="check">
            <input 
              type="checkbox" 
              checked={values.disabilitySupport === 'Yes'} 
              onChange={e => setField('disabilitySupport', e.target.checked ? 'Yes' : 'No')} 
            /> 
            Require accessibility support
          </label>
          <label className="check">
            <input 
              type="checkbox" 
              checked={values.gardenRulesAccepted} 
              onChange={e => setField('gardenRulesAccepted', e.target.checked)} 
              required
            /> 
            I accept the garden rules
          </label>
          <label className="check">
            <input 
              type="checkbox" 
              checked={values.consent} 
              onChange={e => setField('consent', e.target.checked)} 
              required
            /> 
            Consent to data usage
          </label>
        </div>

        <FormRow label="Notes">
          <textarea 
            value={values.notes} 
            onChange={e => setField('notes', e.target.value)} 
            rows={3} 
            className="form-control"
            placeholder="Any additional information..."
          />
        </FormRow>

        <div className="actions mt-4">
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Submitting...' : 'Next: Plot Booking'}
          </button>
          <button 
            type="button" 
            className="btn btn-outline-secondary" 
            onClick={reset}
            disabled={isSubmitting}
          >
            Clear Form
          </button>
        </div>
      </form>
    </div>
  );
}
