import { useEffect, useState } from 'react'
import { usersList, usersUpdateRole } from '../../services/api'

export default function Users() {
  const [users,setUsers] = useState([])
  const [err,setErr] = useState('')

  useEffect(() => {
    (async () => {
      try {
        const { users } = await usersList()
        // Normalize role to capitalized for UI
        setUsers(users.map(u => ({ ...u, role: (u.role || '').charAt(0).toUpperCase() + (u.role || '').slice(1) })))
      } catch (e) { setErr(e.message) }
    })()
  }, [])

  const change = async (id, role) => {
    try {
      const { user } = await usersUpdateRole(id, role.toLowerCase())
      setUsers(prev => prev.map(u => u._id===user._id || u.id===user.id ? { ...user, role: (user.role || '').charAt(0).toUpperCase() + (user.role || '').slice(1) } : u))
    } catch (e) { setErr(e.message) }
  }

  return (
    <div>
      <div className="page-header">
        <h2>User Management</h2>
      </div>
      {err && <p className="error">{err}</p>}
      <div className="card">
        <table className="table">
          <thead><tr><th>Name</th><th>Email</th><th>Role</th><th>Joined</th><th>Actions</th></tr></thead>
          <tbody>
            {users.map(u=>(
              <tr key={u._id || u.id}>
                <td>{u.name}</td>
                <td>{u.email}</td>
                <td>
                  <span className={`badge ${String(u.role).toLowerCase()}`}>{u.role}</span>
                </td>
                <td>{u.createdAt ? new Date(u.createdAt).toLocaleString() : '-'}</td>
                <td>
                  <select value={u.role} onChange={e=>change(u._id || u.id, e.target.value)}>
                    <option>Member</option><option>Manager</option><option>Admin</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  )
}
