import React, { useState, useEffect, useCallback } from 'react';
import { 
  bookingsList, 
  usersList, 
  registrationsList 
} from '../../services/api';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { 
  FiUsers, 
  FiCalendar, 
  FiHome, 
  FiDollarSign,
  FiTrendingUp,
  FiTrendingDown,
  FiSearch,
  FiActivity,
  FiBarChart2,
  FiFilter,
  FiDownload
} from 'react-icons/fi';

// Stat card component
const StatCard = ({ title, value, icon: Icon, color = 'gray', trend, subValue }) => {
  const colorClasses = {
    indigo: 'bg-indigo-100 text-indigo-600',
    green: 'bg-green-100 text-green-600',
    blue: 'bg-blue-100 text-blue-600',
    purple: 'bg-purple-100 text-purple-600',
    red: 'bg-red-100 text-red-600',
    yellow: 'bg-yellow-100 text-yellow-600',
    gray: 'bg-gray-100 text-gray-600'
  };

  return (
    <div className="bg-white rounded-lg shadow p-6">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-medium text-gray-500">{title}</p>
          <div className="flex items-baseline mt-1">
            <p className="text-2xl font-semibold text-gray-900">{value}</p>
            {subValue && <span className="ml-2 text-sm text-gray-500">{subValue}</span>}
          </div>
        </div>
        <div className={`p-3 rounded-lg ${colorClasses[color]}`}>
          <Icon className="h-6 w-6" />
        </div>
      </div>
    </div>
  );
};

export default function ManagerReports() {
  const [reports, setReports] = useState({
    totalUsers: 0,
    activeBookings: 0,
    availablePlots: 0,
    occupiedPlots: 0,
    recentBookings: [],
    monthlyRevenue: 0,
    plotUtilization: 0,
    totalPlots: 50
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [timeRange, setTimeRange] = useState('month');
  const [activeTab, setActiveTab] = useState('overview');
  const [filteredBookings, setFilteredBookings] = useState([]);

  const tableHeaderStyle = {
    padding: '0.75rem 1rem',
    textAlign: 'left',
    fontWeight: '600',
    color: '#374151',
    backgroundColor: '#f9fafb',
    borderBottom: '1px solid #e5e7eb',
  };

  const tableCellStyle = {
    padding: '1rem',
    borderBottom: '1px solid #e5e7eb',
    color: '#4b5563',
  };

  const getStatusBadge = (status) => {
    const statusConfig = {
      active: { bg: 'bg-green-100', text: 'text-green-800' },
      pending: { bg: 'bg-yellow-100', text: 'text-yellow-800' },
      completed: { bg: 'bg-blue-100', text: 'text-blue-800' },
      cancelled: { bg: 'bg-red-100', text: 'text-red-800' },
    };

    const config = statusConfig[status] || { bg: 'bg-gray-100', text: 'text-gray-800' };

    return (
      <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium ${config.bg} ${config.text}`}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </span>
    );
  };

  const fetchReportData = useCallback(async () => {
    try {
      setLoading(true);
      setError('');

      const [bookingsRes, usersRes] = await Promise.all([
        bookingsList(),
        usersList()
      ]);

      const bookings = bookingsRes.bookings || [];
      const users = usersRes.users || [];
      const now = new Date();

      // Calculate active bookings and available plots
      const activeBookings = bookings.filter(b => new Date(b.endDate) >= now);
      const availablePlots = Math.max(0, 50 - activeBookings.length);

      // Prepare recent bookings data
      const recentBookings = bookings
        .filter(booking => new Date(booking.endDate) >= now)
        .sort((a, b) => new Date(a.startDate) - new Date(b.startDate))
        .slice(0, 5)
        .map(booking => ({
          ...booking,
          status: 'active', // Default status
          assignedTo: users.find(u => u._id === booking.userId) || { name: 'Unknown User' },
          lastMaintained: booking.updatedAt || booking.createdAt
        }));

      setReports({
        totalUsers: users.length,
        activeBookings: activeBookings.length,
        availablePlots,
        occupiedPlots: activeBookings.length,
        recentBookings,
        monthlyRevenue: activeBookings.length * 50, // Example calculation: $50 per plot
        plotUtilization: Math.round((activeBookings.length / 50) * 100),
        totalPlots: 50
      });

      setFilteredBookings(recentBookings);
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to fetch report data. Please try again.';
      setError(errorMessage);
      toast.error(errorMessage);
      console.error('Error fetching report data:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchReportData();
  }, [fetchReportData]);

  // Apply search filter
  useEffect(() => {
    if (searchQuery) {
      const filtered = reports.recentBookings.filter(booking => 
        booking.assignedTo.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        booking._id.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredBookings(filtered);
    } else {
      setFilteredBookings(reports.recentBookings);
    }
  }, [searchQuery, reports.recentBookings]);

  const renderOverview = () => (
    <div className="space-y-6">
      {/* Time Range Selector */}
      <div className="flex justify-between items-center">
        <h2 className="text-xl font-semibold text-gray-800">Dashboard Overview</h2>
        <div className="flex space-x-2 bg-gray-100 p-1 rounded-lg">
          {['day', 'week', 'month', 'year'].map((range) => (
            <button
              key={range}
              onClick={() => setTimeRange(range)}
              className={`px-3 py-1 text-sm font-medium rounded-md ${
                timeRange === range
                  ? 'bg-white text-indigo-600 shadow-sm'
                  : 'text-gray-600 hover:text-gray-800'
              }`}
            >
              {range.charAt(0).toUpperCase() + range.slice(1)}
            </button>
          ))}
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-500">Total Users</p>
              <p className="text-2xl font-semibold text-gray-900">{reports.totalUsers}</p>
            </div>
            <div className="p-3 rounded-lg bg-indigo-100 text-indigo-600">
              <FiUsers className="h-6 w-6" />
            </div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-500">Active Bookings</p>
              <p className="text-2xl font-semibold text-gray-900">{reports.activeBookings}</p>
            </div>
            <div className="p-3 rounded-lg bg-green-100 text-green-600">
              <FiCalendar className="h-6 w-6" />
            </div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-500">Available Plots</p>
              <div className="flex items-baseline">
                <p className="text-2xl font-semibold text-gray-900">{reports.availablePlots}</p>
                <span className="ml-2 text-sm text-gray-500">of {reports.totalPlots}</span>
              </div>
            </div>
            <div className="p-3 rounded-lg bg-blue-100 text-blue-600">
              <FiHome className="h-6 w-6" />
            </div>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-500">Monthly Revenue</p>
              <p className="text-2xl font-semibold text-gray-900">
                ${(reports.monthlyRevenue || 0).toLocaleString()}
              </p>
            </div>
            <div className="p-3 rounded-lg bg-purple-100 text-purple-600">
              <FiDollarSign className="h-6 w-6" />
            </div>
          </div>
        </div>
      </div>

      {/* Utilization Section */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        <div className="lg:col-span-2 bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-medium text-gray-900">Plot Utilization</h3>
