import { useEffect, useState } from 'react'
import { registrationsList, bookingsList, toolsList, feedbackList } from '../../services/api'
import { Link } from 'react-router-dom'

function TabBar({ tab, setTab }) {
  const tabs = ['Registrations','Bookings','Tools','Feedback','Users']
  return (
    <div className="tabs">
      {tabs.map(t => (
        <button key={t} className={`tab ${tab===t?'active':''}`} onClick={()=>setTab(t)}>{t}</button>
      ))}
    </div>
  )
}

export default function AdminDashboard() {
  const [tab,setTab] = useState('Registrations')
  const [data,setData] = useState([])
  const [err,setErr] = useState('')

  useEffect(() => {
    (async () => {
      try {
        setErr('')
        if (tab==='Registrations') {
          const { registrations } = await registrationsList(); setData(registrations)
        } else if (tab==='Bookings') {
          const { bookings } = await bookingsList(); setData(bookings)
        } else if (tab==='Tools') {
          const { tools } = await toolsList(); setData(tools)
        } else if (tab==='Feedback') {
          const { feedback } = await feedbackList(); setData(feedback)
        } else if (tab==='Users') {
          setData([]) // navigates via link below
        }
      } catch (e) { setErr(e.message) }
    })()
  }, [tab])

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <TabBar tab={tab} setTab={setTab} />
      {tab==='Users' ? (
        <p>Go to <Link className="btn" to="/users">User Management</Link></p>
      ) : (
        <>
          {err && <p className="error">{err}</p>}
          <div className="table-wrap">
            <table className="table">
              <thead>
                <tr>
                  {data[0] ? Object.keys(data[0]).filter(k=>k!=='password' && k!=='__v').slice(0,6).map(k=> <th key={k}>{k}</th>) : <th>Empty</th>}
                </tr>
              </thead>
              <tbody>
                {data.map((row,i)=> (
                  <tr key={row._id || i}>
                    {Object.keys(row).filter(k=>k!=='password' && k!=='__v').slice(0,6).map(k=> <td key={k}>{String(row[k])}</td>)}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  )
}
