import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import RoleBadge from '../components/RoleBadge';

export default function Dashboard() {
  const { user } = useAuth()
  return (
    <div>
      <h2>Welcome, {user?.name}! <RoleBadge role={user.role} /></h2>
      <p>Use the forms below to complete the workflow:</p>
      <ol className="steps">
        <li><Link to="/form/register">Member Registration</Link></li>
        <li><Link to="/form/booking">Plot Booking</Link></li>
        <li><Link to="/form/confirm-tools">Confirmation & Tools</Link></li>
        <li><Link to="/form/feedback">Feedback & Maintenance</Link></li>
      </ol>
      {['Admin','Manager'].includes(user.role) && (
        <div className="grid">
          <Link className="tile" to="/reports/occupancy">Plot Occupancy</Link>
          <Link className="tile" to="/reports/activity">Member Activity</Link>
          <Link className="tile" to="/reports/trends">Planting Trends</Link>
          <Link className="tile" to="/reports/feedback">Feedback Summary</Link>
        </div>
      )}
    </div>
  )
}
